import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css';

const Transaction = () => {
  const [transactions, setTransactions] = useState([]);
  const [formData, setFormData] = useState({ type: '', amount: '', description: '' });
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchTransactions();
  }, []);

  const fetchTransactions = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/transactions');
      setTransactions(response.data.data);
    } catch (error) {
      console.error('Error fetching transactions:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    if (!formData.type || !formData.amount || !formData.description) {
      alert('Please fill in all fields.');
      return;
    }

    try {
      await axios.post('https://trans-manager-1.onrender.com/api/transactions', formData);
      setFormData({ type: '', amount: '', description: '' });
      setShowForm(false);
      fetchTransactions(); // Fetch updated transactions from the server
    } catch (error) {
      console.error('Error saving transaction:', error);
    }
  };

  const handleCancel = () => {
    setFormData({ type: '', amount: '', description: '' });
    setShowForm(false);
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={isDarkMode ? 'dark-mode' : 'light-mode'}>
      <div className="theme-toggle">
        <label className="switch">
          <input type="checkbox" onChange={toggleTheme} />
          <span className="slider round"></span>
        </label>
      </div>
      <div className="transaction-app">
        <button className="add-transaction-btn" onClick={() => setShowForm(true)}>
          Add Transaction
        </button>

        {showForm && (
          <div className="form-container">
            <div className="form-group">
              <label>Type</label>
              <select name="type" value={formData.type} onChange={handleInputChange}>
                <option value="">Select</option>
                <option value="credit">Credit</option>
                <option value="debit">Debit</option>
              </select>
            </div>
            <div className="form-group">
              <label>Amount</label>
              <input
                type="number"
                name="amount"
                value={formData.amount}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-group">
              <label>Description</label>
              <input
                type="text"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
              />
            </div>
            <div className="form-actions">
              <button className="save-btn" onClick={handleSave}>
                Save
              </button>
              <button className="cancel-btn" onClick={handleCancel}>
                Cancel
              </button>
            </div>
          </div>
        )}

        <table className="transaction-table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Description</th>
              <th>Credit</th>
              <th>Debit</th>
              <th>Total</th>
            </tr>
          </thead>
          <tbody>
            {transactions.reduce((acc, transaction) => {
              const total =
                transaction.type === 'credit'
                  ? acc.total + transaction.amount
                  : acc.total - transaction.amount;
              acc.rows.push(
                <tr key={transaction.id}>
                  <td>{transaction.date}</td>
                  <td>{transaction.description}</td>
                  <td>{transaction.type === 'credit' ? transaction.amount : ''}</td>
                  <td>{transaction.type === 'debit' ? transaction.amount : ''}</td>
                  <td>{total}</td>
                </tr>
              );
              acc.total = total;
              return acc;
            }, { rows: [], total: 0 }).rows}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Transaction;
